import React from 'react';
import Pact from 'pact-lang-api';
import config from '../var/config';

const Context = React.createContext();

const dumKeyPair = Pact.crypto.genKeyPair();

const savedAcct = localStorage.getItem('acct');

export class PactStore extends React.Component {

  state = {
    accountName: (savedAcct ? savedAcct : ""),
    coinBalance: "n/a",
    loading: false,
    reqKey: "",
    msg: "",
    err: false,
    be: '',
    hash: ""
  }

  convertDecimal = (decimal) => {
    decimal = decimal.toString();
    if (decimal[0] === ".") {return "0" + decimal}
    if (decimal.includes('.')) { return decimal }
    if ((decimal / Math.floor(decimal)) === 1) {
      decimal = decimal + ".0"
    }
    return decimal
  }

  setAccountName = async (str) => {
    this.setState({ accountName: str })
    await localStorage.setItem('acct', str);
  }

  getCoinBalance = async () => {
    const cmd = await Pact.fetch.local
      ({
        pactCode: `(user.silver-token.get-balance ${JSON.stringify(this.state.accountName)})`,
        keyPairs: dumKeyPair,
        caps:
          [
            Pact.lang.mkCap("Gas capability", "description of gas cap", "coin.GAS", [])
          ],
        meta: Pact.lang.mkMeta
          (
            config.meta.sender,
            config.meta.chainId,
            config.meta.gasPrice,
            config.meta.gasLimit,
            config.meta.creationTime(),
            config.meta.ttl
          )
      },
      config.meta.host
      )
    const data = await cmd;
    if (data.status === "success") {
      await this.setState({ coinBalance: data.data.toString().substring(0,15) })
    } else {
      await this.setState({ coinBalance: "n/a" })
    }
  }

  listen = async (reqKey) => {
    const res = await Pact.fetch.listen({listen: reqKey}, config.meta.host);
    console.log(res)
    if (res.status === 'success') {
      this.setState({ msg: 'Success! Check out your '})
    } else {
      this.setState({ msg: 'There was an error processing your '})
    }
  }

  sendST = async (acct, amount) => {
    amount = this.convertDecimal(amount)
    try {
      const signCmd = {
          pactCode: `(user.silver-token.transfer ${JSON.stringify(this.state.accountName)} ${JSON.stringify(acct)} ${amount})`,
          caps: [
            // Pact.lang.mkCap("Gas capability", "description of gas cap", "user.st-gas-station.GAS_PAYER", ["hi", {int: 1}, 1.0]),
            Pact.lang.mkCap("Gas capability", "description of gas cap", "coin.GAS", []),
            Pact.lang.mkCap("transfer capability", "description of transfer cap", "user.silver-token.TRANSFER", [this.state.accountName, acct, parseFloat(amount)])
          ],
          sender: this.state.accountName,
          gasLimit: 5000,
          chainId: "3",
          ttl: 28800,
          envData: {}
        }

      console.log(signCmd)
      const cmd = await Pact.wallet.sign(signCmd)
      console.log(cmd)
    } catch(err){
      console.log(err);
      alert("you cancelled the TX or you did not have the wallet app open")
      window.location.reload();
    }
  }

  mint = async (amount) => {
    //conf param is to be IMPLEMENTED
    //  left as a placeholder to check on backend that the fiat payment was received
    this.setState({ loading: true, msg: "" })
    try {
      const res = await fetch(`https://silver-token-api.chainweb.com/transaction/mint`, {
        headers: {
          "Content-Type": "application/json"
        },
        method: "POST",
        body: JSON.stringify({
          type: "mint",
        	tx: {
        		account: this.state.accountName,
        		amount: amount,
        		conf: "IMPLEMENT ME"
        	}
        })
      })
      const json = await res.json();
      const reqKey = json.reqkey;
      this.setState({ reqKey: reqKey, be: `https://explorer.chainweb.com/${config.meta.networkId.slice(0, -2)}/tx/${reqKey}` })
      await this.listen(reqKey)
    } catch (e) {
      console.log(e);
      this.setState({ loading: false, msg: "" })
      alert('There was a problem processing your transaction. Please try again')
    }

  }

  burn = async (amount) => {
    this.setState({ loading: true, msg: "" })
    try {
      const res = await fetch(`https://silver-token-api.chainweb.com/transaction/burn`, {
        headers: {
          "Content-Type": "application/json"
        },
        method: "POST",
        body: JSON.stringify({
          type: "burn",
        	tx: {
        		account: this.state.accountName,
        		amount: amount,
        	}
        })
      })
      const json = await res.json();
      const reqKey = json.reqkey;
      this.setState({ reqKey: reqKey, be: `https://explorer.chainweb.com/${config.meta.networkId.slice(0, -2)}/tx/${reqKey}` })
      await this.listen(reqKey)
    } catch (e) {
      console.log(e);
      this.setState({ loading: false, msg: "" })
      alert('There was a problem processing your transaction. Please try again')
    }

  }

  transfer = async (recp, amount) => {
    this.setState({ loading: true, msg: "" })
    try {
      const res = await fetch(`https://silver-token-api.chainweb.com/transaction/transfer`, {
        headers: {
          "Content-Type": "application/json"
        },
        method: "POST",
        body: JSON.stringify({
          type: "transfer",
          tx: {
            from: this.state.accountName,
            to: recp,
            amount: amount,
          }
        })
      })
      const json = await res.json();
      const reqKey = json.reqkey;
      this.setState({ reqKey: reqKey, be: `https://explorer.chainweb.com/${config.meta.networkId.slice(0, -2)}/tx/${reqKey}` })
      await this.listen(reqKey)
    } catch (e) {
      console.log(e);
      this.setState({ loading: false, msg: "" })
      alert('There was a problem processing your transaction. Please try again')
    }

  }

  download = (data, filename, type) => {
    var file = new Blob([data], {type: type});
    if (window.navigator.msSaveOrOpenBlob) // IE10+
        window.navigator.msSaveOrOpenBlob(file, filename);
    else { // Others
        var a = document.createElement("a"),
                url = URL.createObjectURL(file);
        a.href = url;
        a.download = filename;
        document.body.appendChild(a);
        a.click();
        setTimeout(function() {
            document.body.removeChild(a);
            window.URL.revokeObjectURL(url);
        }, 0);
    }
  }

  genKp = async () => {
    var kp = Pact.crypto.genKeyPair();
    var id = kp.publicKey.substring(0, 6);
    var priv = `public: ${kp.publicKey}\nsecret: ${kp.secretKey}`
    var privName = `private-keypair-${id}.kda`
    await this.setAccountName(kp.publicKey);
    this.download(priv, privName, "text/plain;charset=utf-8");

  }

  transferDirect = (recp, amount, privKey) => {
    const amt = this.convertDecimal(amount);
    const pactCode = `(user.silver-token.transfer ${JSON.stringify(this.state.accountName)} ${JSON.stringify(recp)} ${amt})`
    const m = Pact.lang.mkMeta
      (
        config.meta.sender,
        config.meta.chainId,
        config.meta.gasPrice,
        config.meta.gasLimit,
        config.meta.creationTime(),
        config.meta.ttl
      );
    const cmd = Pact.simple.exec.createCommand
      (
        {
          publicKey: this.state.accountName,
          secretKey: privKey,
          clist:
            [
              {name: "user.st-gas-station.GAS_PAYER", args: ["hi", {int: 1}, 1.0]},
              {name: "user.silver-token.TRANSFER", args: [this.state.accountName, recp, parseFloat(amount)]}
            ]
        },
        JSON.stringify(new Date()),
        pactCode,
        {},
        m,
        config.meta.networkId
      )
    console.log(cmd)
  }



  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          setAccountName: this.setAccountName,
          getCoinBalance: this.getCoinBalance,
          sendST: this.sendST,
          mint: this.mint,
          burn: this.burn,
          transfer: this.transfer,
          genKp: this.genKp
        }}
      >
        {this.props.children}
      </Context.Provider>
    );
  }

}

export default Context;
