import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader, Menu } from 'semantic-ui-react';
import PactContext from "../contexts/PactContext";

const TXStatus = () => {

  const pactContext = useContext(PactContext);


  const status = () => {
    return (
      <div>
        <Form>
          <Message positive style={{marginLeft: 50, marginRight: 50, marginTop: 20, textAlign: 'center'}}>
            <Message.Header style={{marginTop: 5}}>
              Request Key: {pactContext.reqKey}
            </Message.Header>
            {(pactContext.msg === ""
            ?
              <Loader />
            :
              <div></div>
            )}
            <div style={{marginBottom: 10, marginTop: 10}}>
              You will be charged the USD equivalent
            </div>
          </Message>
        </Form>
      </div>
    )
  }

  return (
    <div>
      {status()}
    </div>
  );

}

export default TXStatus;
