import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader, Menu } from 'semantic-ui-react';
import PactContext from "../contexts/PactContext";

const User = () => {

  const pactContext = useContext(PactContext);

  const pactFecth = async () => {
    await pactContext.getCoinBalance();
  }

  const [action, setAction] = useState('send')
  const [recp, setRecp] = useState("")
  const [amount, setAmount] = useState("")

  const send = () => {
    return (
      <div>
        <Form>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 20, textAlign: "left"}} >
            <label style={{color: "#054F9E" }}>Recepient Account Name
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is Account Name? </Popup.Header>
              <Popup.Content>Account Name is the unique sequence of characters that you use to identify a BiLira account. NOTE: if you send to the wrong account it is IRREVERSIBLE</Popup.Content>
              </Popup>
            </label>
              <Form.Input
                icon='user'
                iconPosition='left'
                placeholder='Recepient'
                value={recp}
                onChange={(e) => setRecp(e.target.value)}
              />
          </Form.Field>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "0px", marginBottom: 20, textAlign: "left"}} >
            <label style={{color: "#054F9E" }}>Amount to Send
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
                <Popup.Header>What is Amount to Send? </Popup.Header>
                <Popup.Content>Quantity of BiLira you would like to send to specified recepient</Popup.Content>
              </Popup>
            </label>
              <Form.Input
                icon='dollar sign'
                iconPosition='left'
                placeholder='Amount'
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />
          </Form.Field>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "0px", marginBottom: 30, textAlign: "center"}} >
          <Button
              style={{
                backgroundColor: "#054F9E",
                color: "white",
                marginBottom: 10,
                marginTop: 20,
                width: 340,
                }}
              onClick={() => pactContext.sendST(recp, amount)}
              disabled={!pactContext.accountName}
            >
            Send
          </Button>
          </Form.Field>
        </Form>

      </div>
    )
  }

  const redeem = () => {
    return (
      <div>
        <Form>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 20, textAlign: "left"}} >
            <label style={{color: "#054F9E" }}>Amount to Redeem
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
                <Popup.Header>What is Amount to Redeem? </Popup.Header>
                <Popup.Content>Turn your tokenized silver back into a physical asset</Popup.Content>
              </Popup>
            </label>
              <Form.Input
                icon='dollar sign'
                iconPosition='left'
                placeholder='Amount'
                // value={amountBuy}
                // onChange={(e) => setAmountBuy(e.target.value)}
              />
          </Form.Field>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "0px", marginBottom: 30, textAlign: "center"}} >
          <Button
              style={{
                backgroundColor: "#054F9E",
                color: "white",
                marginBottom: 10,
                marginTop: 20,
                width: 340,
                }}
              // onClick={() => pactFecth()}
              // disabled={!pactContext.accountName}
            >
            Redeem
          </Button>
          </Form.Field>
        </Form>
      </div>
    )
  }

  return (
    <div style={{}}>
    <Form>
      <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 30, textAlign: "left"}} >
        <label style={{color: "#054F9E" }}>Enter Your Public Key
          <Popup
            trigger={
              <Icon name='help circle' style={{"marginLeft": "2px"}}/>
            }
            position='top center'
          >
          <Popup.Header>What is a Public Key? </Popup.Header>
          <Popup.Content>Account Name is the unique sequence of characters that you use to identify yourself in chainweb. You'll be asked to sign with associated key/keys when you make transactions. Account names need to be unique and are assosciated to keypairs that can sign its transactions. The simplest way would be to use your public key as your account name</Popup.Content>
          </Popup>
        </label>
          <Form.Input
            icon='key'
            iconPosition='left'
            placeholder='Account Name'
            value={pactContext.accountName}
            onChange={(e) => pactContext.setAccountName(e.target.value)}
            action={
               <Button
               color='grey'
               onClick={() => pactContext.getCoinBalance()}
               >
                <Icon name="redo"/>
                refresh
               </Button>
             }
          />
      </Form.Field>
    </Form>
    <Message color="blue" style={{marginLeft: 100, marginRight: 100}}>
      <Message.Header>
        BiLira Balance: {pactContext.coinBalance} STX
      </Message.Header>
    </Message>
    <div style={{marginLeft: 50, marginRight: 50, marginTop: 40}}>
    <Menu color="blue" widths={3} >
          <Menu.Item
            name='buy'
            active={action === 'buy'}
            onClick={() => setAction('buy')}
          >
            Buy
          </Menu.Item>

          <Menu.Item
            name='redeem'
            active={action === 'redeem'}
            onClick={() => setAction('redeem')}
          >
            Sell
          </Menu.Item>

          <Menu.Item
            name='send'
            active={action === 'send'}
            onClick={() => setAction('send')}
          >
            Send
          </Menu.Item>

        </Menu>
      </div>
      {(action === 'send'
      ?
        send()
      :
        redeem()
      )}
    </div>
  );

}

export default User;
