/**
Module: (index) react-frontend/Home.js
Copyright: Copyright © 2018 - 2020 Kadena LLC.
License: MIT
Maintainer: Francesco Melpignano <francesco@kadena.io>
Stability: experimental

Home page for react frontend
**/

import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import User from './User';
import Admin from './Admin';
import Info from './Info';
import PactContext from "../contexts/PactContext";


const Home = () => {

  const pactContext = useContext(PactContext);

  useEffect(() => {
    async function pactFetch() {
      await pactContext.getCoinBalance();
    }
    pactFetch();
  }, [])


  const panes = [
    { menuItem: { key: 'user', icon: 'user', content: 'Account Details' },
      render: () => <Tab.Pane>
                      <User />
                    </Tab.Pane>
    },
    { menuItem: { key: 'user plus', icon: 'user plus', content: 'Create Account' },
      render: () => <Tab.Pane>
                      <Admin />
                    </Tab.Pane>
    }
  ]

  return (
    <Grid columns={2} padded scrollable verticalAlign="top" style={{backgroundColor: 'grey'}}>
      <Grid.Column textAlign="center" style={{overflow: "auto", backgroundColor: 'grey'}}>
        <Info />
      </Grid.Column>
      <Grid.Column style={{overflow: "auto", backgroundColor: "#054F9E"}}>
        <div style={{overflow: "auto", height: "100vh"}}>
        <Tab
        panes={panes}
          menu={{ pointing: true, color:'grey', inverted: true, attached: true }}
          style={{marginLeft: 40, marginRight: 40, marginTop: 20, height: document.documentElement.offsetHeight}}/>
        </div>
      </Grid.Column>
    </Grid>

  );
}

export default Home;
