/**
Module: components/Home.js
Copyright: Copyright Â© 2018 - 2020 Kadena LLC.
License: MIT
Maintainer: Francesco Melpignano <francesco@kadena.io>
Stability: experimental

Home page for react frontend
**/

import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import User from './User';
import Admin from './Admin';
import PactContext from "../contexts/PactContext";


const Home = () => {

  const pactContext = useContext(PactContext);

  useEffect(() => {
    async function pactFetch() {
      await pactContext.getCoinBalance();
    }
    pactFetch();
  }, [])



  const panes = [
    { menuItem: { key: 'user', icon: 'user', content: 'Account Details' },
      render: () => <Tab.Pane>
                      <User />
                    </Tab.Pane>
    }
  ]



  return (
    <Grid columns={2} padded scrollable verticalAlign="top" style={{backgroundColor: 'grey', backgroundImage: require('../assets/kadena.png')}}>
      <Grid.Column textAlign="center" style={{overflow: "auto", backgroundColor: 'grey'}}>
        <Grid.Row style={{marginTop: 80}}>
        <img src={require("../assets/alteum.png")} style={{height:75, marginRight: 20}}/>
        <Icon name='close' style={{height: 40, color: 'white'}}/>
        <img src={require("../assets/kadena.png")} style={{height:70, marginLeft: 30}}/>
        </Grid.Row>

        <Header as="h6" style={{color:'white', fontWeight: 'bold', fontSize: 40, marginTop: 20}}>
          BiLira Demo
        </Header>
        <Message color="blue" style={{marginLeft: 100, marginRight: 100, marginTop: 50}}>

          <img src={require("../assets/dollar.png")} style={{height:40, marginTop: 10}}/>
          <img src={require("../assets/silver.png")} style={{height:40, marginTop: 10, marginLeft: 10}}/>
          <img src={require("../assets/dollar.png")} style={{height:40, marginTop: 10, marginLeft: 10}}/>
          <Message.Header style={{marginTop: 20}}>
            Interact with tokenized physical silver assets
          </Message.Header>
          <div style={{marginTop: 20}}>
            buy and sell BiLira (STS) through the platform
          </div>
          <div style={{marginTop: 5, marginBottom: 5}}>
            or
          </div>
          <div style={{marginTop: 0, marginBottom: 20}}>
            send your BiLira (STS) to other existing users
          </div>
        </Message>

      </Grid.Column>
      <Grid.Column style={{overflow: "auto", backgroundColor: "#054F9E"}}>
        <div style={{overflow: "auto", height: "100vh"}}>
        <Tab
        panes={panes}
          menu={{  color:'grey', inverted: true, attached: true }}
          style={{marginLeft: 40, marginRight: 40,marginTop: 60, height: document.documentElement.offsetHeight}}/>
        </div>
      </Grid.Column>
    </Grid>

  );
}

export default Home;
