import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import User from './User';
import Admin from './Admin';
import PactContext from "../contexts/PactContext";


const Info = () => {


  return (
      <div>
        <Grid.Row style={{marginTop: 80}}>
        <img src={require("../assets/bilira.png")} style={{height:80}}/>
        <img src={require("../assets/logo.png")} style={{height:80, marginLeft: 0, marginTop: 0}}/>
        </Grid.Row>
        <Message color="blue" style={{marginLeft: 100, marginRight: 100, marginTop: 30}}>
          <img src={require("../assets/kadena.png")} style={{height:70}}/>
          <Message.Header style={{marginTop: 20, fontSize: 20}}>
            Your connection to the decentralized internet
          </Message.Header>
          <div style={{marginTop: 20}}>
            buy and sell BiLira (TRYB) through the platform
          </div>
          <div style={{marginTop: 5, marginBottom: 5}}>
            or
          </div>
          <div style={{marginTop: 0, marginBottom: 20}}>
            send your BiLira (TRYB) to other existing users
          </div>
          <div style={{marginTop: 40, display: 'flex', alignItems: 'flex-end'}}>
          </div>
          <Grid.Row>
          <div>
          testnet
          </div>
          <Radio
            // style={{marginLeft: 10, marginRight: 10}}
            slider />
          mainnet
          </Grid.Row>
        </Message>

      </div>


  );
}

export default Info;
