import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader } from 'semantic-ui-react';
import User from './User';
import Admin from './Admin';
import PactContext from "../contexts/PactContext";


const Info = () => {


  return (
      <div>
        <Grid.Row style={{marginTop: 80}}>
        <img src={require("../assets/bilira.png")} style={{height:75, marginRight: 20}}/>
        <Icon name='close' style={{height: 40, color: 'white'}}/>
        <img src={require("../assets/kadena.png")} style={{height:70, marginLeft: 30}}/>
        </Grid.Row>
        <Message color="blue" style={{marginLeft: 100, marginRight: 100, marginTop: 30}}>
          <Message.Header style={{marginTop: 20}}>
            BiLira for the digital era
          </Message.Header>
          <div style={{marginTop: 10}}>
            Your connection to the decentralized internet
          </div>
          <div style={{marginTop: 20}}>
            buy and sell BiLira (TRYB) through the platform
          </div>
          <div style={{marginTop: 5, marginBottom: 5}}>
            or
          </div>
          <div style={{marginTop: 0, marginBottom: 20}}>
            send your BiLira (TRYB) to other existing users
          </div>
        </Message>
      </div>


  );
}

export default Info;
