import React, { useState, useEffect, useContext } from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Button, Grid, Input, Icon, Form, List,
   Modal, Header, Message, Popup, Select, Radio,
   Tab, TextArea, Loader, Menu } from 'semantic-ui-react';
import TXStatus from './TXStatus';
import PactContext from "../contexts/PactContext";

const Send = () => {

  const pactContext = useContext(PactContext);

  const [recp, setRecp] = useState("")
  const [amount, setAmount] = useState("")
  const [action, setAction] = useState('plat')

  const send = () => {
    return (
      <div>
        <Form>
          <Message color="yellow" style={{marginLeft: 100, marginRight: 100, marginTop: 30, textAlign: 'center'}}>
            <Message.Header style={{marginTop: 20, fontSize: 16}}>
              Send BiLira (STS) to a different account
            </Message.Header>
            <div style={{marginBottom: 5, marginTop: 10}}>
              Be sure of the receiver as blockchain transactions cannot be reversed
            </div>
          </Message>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "20px", marginBottom: 20, textAlign: "left"}} >
            <label style={{color: "#054F9E" }}>Recepient Public Key
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
              <Popup.Header>What is Public Key? </Popup.Header>
              <Popup.Content>Public Key is the unique sequence of characters that you use to identify a BiLira account. NOTE: if you send to the wrong existing account it can be IRREVERSIBLE</Popup.Content>
              </Popup>
            </label>
              <Form.Input
                icon='user'
                iconPosition='left'
                placeholder='Recepient'
                value={recp}
                disabled={pactContext.loading}
                onChange={(e) => setRecp(e.target.value)}
              />
          </Form.Field>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "0px", marginBottom: 20, textAlign: "left"}} >
            <label style={{color: "#054F9E" }}>(STS) Amount to Send
              <Popup
                trigger={
                  <Icon name='help circle' style={{"marginLeft": "2px"}}/>
                }
                position='top center'
              >
                <Popup.Header>What is Amount to Send? </Popup.Header>
                <Popup.Content>Quantity of BiLira you would like to send to specified recepient</Popup.Content>
              </Popup>
            </label>
              <Form.Input
                icon='dollar sign'
                iconPosition='left'
                placeholder='Amount (STS)'
                value={amount}
                disabled={pactContext.loading}
                onChange={(e) => setAmount(e.target.value)}
              />
              {(parseFloat(amount)
                ?
                  `~$${(parseFloat(amount) * 0.63).toFixed(2)}`
                :
                  (amount === ""
                    ?
                      ""
                    :
                      "please enter a digit"
                  )
              )}
          </Form.Field>
          <div style={{marginLeft: 50, marginRight: 50, marginTop: 20, marginBottom: 10}}>
          <Menu color="blue" widths={2} >
                <Menu.Item
                  name='buy'
                  active={action === 'plat'}
                  onClick={() => setAction('plat')}
                >
                  <Icon name='money bill alternate' />
                  Send Through Platform
                </Menu.Item>

                <Menu.Item
                  name='sign'
                  active={action === 'sign'}
                  onClick={() => setAction('sign')}
                  disabled={pactContext.coinBalance === 'n/a'}
                >
                  <Icon name='exchange' />
                  Sign and Send to Blockchain
                </Menu.Item>

              </Menu>
            </div>
          <Form.Field  style={{marginLeft: 50, marginRight: 50, marginTop: "0px", marginBottom: 30, textAlign: "center"}} >
            {(pactContext.loading
            ?
              <TXStatus />
            :
              <Button
                  style={{
                    backgroundColor: "#054F9E",
                    color: "white",
                    marginBottom: 10,
                    marginTop: 20,
                    width: 340,
                    }}
                  onClick={() => pactContext.transfer(recp, amount)}
                  disabled={!pactContext.accountName || amount === "" || isNaN(amount) || recp.length !== 64}
                  loading={pactContext.loading}
                >
                Send
              </Button>
            )}
          </Form.Field>
        </Form>

      </div>
    )
  }

  return (
    <div>
      {send()}
    </div>
  );

}

export default Send;
